'use strict';

/**
 * @ngdoc overview
 * @name To Do App
 * @description
 * # To do Application
 *
 * Main module of the application.
 */
var app = angular.module('chefUni', [
    'ngAnimate',
    'ngAria',
    'ngCookies',
    'ngMessages',
    'ngResource',
    'ngRoute',
    'ngSanitize',
    'ngTouch'
  ])


app.config(function ($routeProvider) {
    $routeProvider
      .when('/', {
        templateUrl: 'node_modules/partials/welcome.html'
      })
     .when('/create', {
        templateUrl: 'node_modules/partials/create.html'
      })
     .when('/recipes', {
        templateUrl: 'node_modules/partials/recipeList.html',
        controller: 'recipeListCtrl'
      })
      .otherwise({
        redirectTo: '/'
      });
});