'use strict';

/**
 * @ngdoc overview
 * @name To Do App
 * @description
 * # To do Application
 *
 * Main module of the application.
 */
var app = angular.module('chefUni', [
    'ngAnimate',
    'ngAria',
    'ngCookies',
    'ngMessages',
    'ngResource',
    'ngRoute',
    'ngSanitize',
    'ngTouch',
  ])


app.config(function ($routeProvider) {
    $routeProvider
      .when('/', {
        templateUrl: 'node_modules/partials/welcome.html'
      })
     .when('/create', {
        templateUrl: 'node_modules/partials/create.html'
      })
     .when('/recipes', {
        templateUrl: 'node_modules/partials/recipeList.html',
        controller: 'recipeListCtrl'
      })
       .when('/recipes/:recipeId', {
       templateUrl: 'node_modules/partials/recipeView.html',
        controller: 'recipeViewCtrl'
      })
     .when('/chefs', {
        templateUrl: 'node_modules/partials/chefList.html',
        controller: 'chefListCtrl'
      })
      .when('/chefs/:chefID', {
       templateUrl: 'node_modules/partials/chefView.html',
        controller: 'chefViewCtrl'
      })
       .when('/login', {
        templateUrl: 'node_modules/partials/login.html',
      })

      .otherwise({
        redirectTo: '/'
      });
});