'use strict';
angular.module('chefUni')
.controller('chefListCtrl',  ['$scope', '$rootScope', '$http',
  function($scope,  $rootScope, $http) {
    //$rootScope.recipeId = {}
        $scope.getChefs = function() {
            $http.get('/data/chefList.json')
            .success(function(data, status, headers, config) {
               $scope.chefs = data;
            });
        };
        $scope.clickOnChef = function(id) {
                  $rootScope.chefID = id;
          }
    }

]);
