'use strict';
angular.module('chefUni')
.controller('chefViewCtrl',  ['$scope', '$rootScope', '$http',
  function($scope,  $rootScope, $http) {
        $scope.getChefsView = function() {
            $http.get('/data/chefList.json')
            .success(function(data, status, headers, config) {

               $rootScope.chefID;
               for (var i = 0; i < data.length; i++) {
                if ($rootScope.chefID == data[i].chefID)
                    {
                    $scope.chef = data[i];
                    }
               }
            });
        };
    }
]);