'use strict';
angular.module('chefUni')
.controller('recipeListCtrl',  ['$scope', '$rootScope', '$http',
  function($scope,  $rootScope, $http) {
    //$rootScope.recipeId = {}
        $scope.getRecipes = function() {
            $http.get('/data/recipeList.json')
            .success(function(data, status, headers, config) {
               $scope.recipes = data;
            });
        };
        $scope.clickOnRecipe = function(id) {
                  $rootScope.recipeId = id;
          }
    }

]);
